
// Vanta Cells Fire/Accent Theme Initialization
window.addEventListener('DOMContentLoaded', function() {
  if (window.VANTA && window.VANTA.CELLS) {
    VANTA.CELLS({
      el: "#background",
      mouseControls: true,
      touchControls: true,
      gyroControls: false,
      minHeight: 200.00,
      minWidth: 200.00,
      scale: 1.00,
      color1: 0xff4c00,      // accent (orange)
      color2: 0x6e604b,      // primary (brown/bronze)
      backgroundColor: 0x000000,
      amplitudeFactor: 1.1,
      ringFactor: 1.05,
      rotationFactor: 1.2,
      size: 2.6,
      speed: 1.6
    });
  } else {
    console.error("VANTA failed to load.");
    document.getElementById('background').style.background = '#221a0f';
  }
});
